
public class CountDown {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int n = 20; // Interger is from 20
		do {
			System.out.println(" - " + n); // it prints out "-" + 20 seconds
			n--; // -- means it will go negitive ++ will break it & will conduct a loop till I stop the run.
		} while(n > 0);

	}

}
